config timeframe = 7d|
dataset = incidents
//get created incidents by day
| bin creation_time span = 1d
| alter day = format_timestamp("%d", creation_time), month = format_timestamp("%b", creation_time), status_str = if(status in(ENUM.RESOLVED, ENUM.RESOLVED_AUTO_RESOLVE, ENUM.RESOLVED_DUPLICATE_INCIDENT, ENUM.RESOLVED_FALSE_POSITIVE, ENUM.RESOLVED_HANDLED_THREAT, ENUM.RESOLVED_KNOWN_ISSUE, ENUM.RESOLVED_OTHER, ENUM.RESOLVED_SECURITY_TESTING), "Troy Training - Resolved", status in(ENUM.NEW, ENUM.UNDER_INVESTIGATION), "Active Investigation", "True Positive - Resolved")
//|filter month contains "Aug"
| comp count_distinct(incident_id) as new by creation_time, day, month, status_str

//display in grouped vertical columns
| sort asc creation_time  


| view graph type = column subtype = grouped layout = horizontal xaxis = month yaxis = new series = status_str default_limit = `false`
